import React,{useState,useEffect} from 'react'

const Projects = () => {

    const [state,setState,] = useState({
        India:100,
        Pakistan:70
    })

    const CountIndia = state.India
    const CountPakistan = state.Pakistan

    const Iplus = () => {
        setState({...state,India: state.India})
    }
    
    const Pplus = () => {
        setState({...state,Pakistan: state.Pakistan})
    }

    return(

        <div>
            <h1>India = {CountIndia}</h1>
            <h1>Pakistan = {CountPakistan}</h1>
            <button onClick={Iplus}>India +</button>
            <button onClick={Pplus}>Pakistan +</button>
            
           
        </div>

    )        

}
export default Projects
