import React,{useState,useEffect} from 'react'

const Projects = () => {

    const [state,setState,] = useState({
        India:100,
        Pakistan:70
    })

    const CountIndia = state.India
    const CountPakistan = state.Pakistan

    const Iminus =() =>{
        setState({...state,India: state.India-1})
    }
    const Pminus =() =>{
        setState({...state,India: state.Pakistan-1})
    }
    const Iplus = () => {
        setState({...state,India: state.India +1})
    }
    
    const Pplus = () => {
        setState({...state,Pakistan: state.Pakistan+1})
    }

    return(

        <div style>
            <button onClick={Iminus}>India -</button>
            <button onClick={Pminus}>Pakistan -</button>
            <h1>India = {CountIndia}</h1>
            <h1>Pakistan = {CountPakistan}</h1>
            <button onClick={Iplus}>India +</button>
            <button onClick={Pplus}>Pakistan +</button>
            
           
        </div>

    )        

}
export default Projects
