import React,{useState,useEffect} from 'react'

const Projects = () => {

    const [count,setCount] = useState(100)

    const minus = () => {
        setCount(
            ()=>{
                return(
                    count-1
                )
            }
        )
    }

    const plus = () => {
        setCount(
            ()=>{
                return(
                    count+1
                )
            }
        )
    }

    return(

        <div>
            <button onClick={minus}>-</button>
            <h1>India ={count}</h1>
            <button onClick={plus}>+</button>
        </div>

    )        
 
}
export default Projects
